import Vue from "vue";

const store = Vue.observable({
  isNavOpen: false
});

export const mutations = {
  setIsNavOpen(yesno) {
    store.isNavOpen = yesno;
  },
  toggleNav() {
    store.isNavOpen = !store.isNavOpen;
  }
};

export default store;
